result:
.theyTalked {
  height: 450px;
  @screen ss {
    height: 640px;
  }
  @screen sm {
    height: 480px;
  }
  @screen md {
    height: 500px;
  }
  @screen lg {
    height: 520px;
  }
  @screen xl {
    height: 580px;
  }
}
.super-lines {
  @screen sm {
    display: flex !important;
  }
  .line {
    cursor: pointer;
    background: #626795;
    &--active {
      background: #2d53b2;
    }
    &:hover {
      opacity: 0.9;
    }
  }
}
.button-circle {
  &:hover {
    opacity: 0.9;
  }
}
.circle {
  background: #332f65;
}
.dark {
  .circle {
    background: #a14ff0;
  }
  .super-lines {
    .line {
      background: #626795;
      &--active {
        background: #a6f2f9;
      }
    }
  }
}
::v-deep .carousel-slide {
  display: flex;
  justify-content: center;
}
::v-deep .carousel-arrow {
  position: absolute;
  bottom: 20px;
  top: unset;
  left: 0;
  right: 0;
  margin-left: auto;
  margin-right: auto;
  @screen lg {
    bottom: unset;
    position: unset;
  }
  .has-icons-left {
    border: 1px solid #626795;
    left: calc(50% - 60px) !important;
    height: 40px;
    width: 40px;
    background-color: transparent !important;
    background: url(/images/extra/arrow-left.svg);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 12px;
    @screen lg {
      left: 10px !important;
    }
    &:hover {
      opacity: 0.5;
    }
  }
  .has-icons-right {
    border: 1px solid #626795;
    right: calc(50% - 60px) !important;
    height: 40px;
    width: 40px;
    background-color: transparent !important;
    background: url(/images/extra/arrow-right.svg);
    background-repeat: no-repeat;
    background-position: center;
    background-size: 12px;
    @screen lg {
      right: 10px !important;
    }
    &:hover {
      opacity: 0.5;
    }
  }
}
.dark {
  ::v-deep .carousel-arrow {
    .has-icons-left {
      border: 1px solid #b9bbce;
    }
    .has-icons-right {
      border: 1px solid #b9bbce;
    }
  }
}
