import { Vue, Component, Prop } from 'vue-property-decorator'

@Component({
  components: {},
})
export default class FancyActionButton extends Vue {
  @Prop({
    default: {
      color: '#332f65',
      colorDark: 'white',
      backgroundColor: 'transparent',
      borderColor: '#332f65',
      borderColorDark: '#332f65',
    },
  })
  styles: any
  @Prop() name: string

  get isDark() {
    return this.$store.state.theme.color === 'dark'
  }
}
